export default {
  data: function data() {
    return {
      uname: "",
      upwd: ""
    };
  },
  methods: {
    login: function login() {
      var _this = this;

      var $uname = this.uname;
      var regu = /^[\u4E00-\u9FA5|\w]{3,6}$/;

      if (!regu.test($uname)) {
        this.$messagebox("用户名格式不正确");
        return;
      }

      var $upwd = this.upwd;
      var regp = /^[\d\w\S]{3,6}$/;

      if (!regp.test($upwd)) {
        this.$messagebox("密码不正确");
        return;
      }

      console.log($uname);
      console.log($upwd);
      var url = "userLogin";
      var obj = {
        uname: $uname,
        upwd: $upwd
      };
      this.axios.get(url, {
        params: obj
      }).then(function (result) {
        var code = result.data.code;

        if (code == 0) {
          _this.$messagebox("用户名或密码有误");
        } else {
          _this.$router.push("/product");
        }

        console.log(result);
      }).catch(function (err) {
        console.log(err);
      });
    }
  }
};
